import { z } from '@hono/zod-openapi'

export const ErrorResponseSchema = z.object({
  success: z.literal(false),
  error: z.string().openapi({
    example: 'Bad Request',
    description: 'エラーの種類'
  }),
  message: z.string().openapi({
    example: '無効なリクエストパラメータです',
    description: 'エラーの詳細メッセージ'
  }),
  details: z.any().optional().openapi({
    description: 'エラーの詳細情報'
  })
})

export type ErrorResponse = z.infer<typeof ErrorResponseSchema>

import { z } from '@hono/zod-openapi'

// 埋め込み生成スキーマ
export const GenerateEmbeddingSchema = z.object({
  text: z.string().min(1).openapi({
    example: 'This is a sample text to generate embeddings',
    description: '埋め込みを生成するテキスト'
  }),
  model: z.string().optional().openapi({
    example: '@cf/baai/bge-base-en-v1.5',
    description: '使用するモデル名'
  })
})

export const GenerateEmbeddingResponseSchema = z.object({
  success: z.boolean(),
  data: z.object({
    jobId: z.string(),
    workflowId: z.string(),
    status: z.string()
  }),
  message: z.string()
})

// バッチ埋め込み生成スキーマ
export const BatchEmbeddingSchema = z.object({
  texts: z.array(z.string()).min(1).openapi({
    example: ['Text 1', 'Text 2', 'Text 3'],
    description: '埋め込みを生成するテキストの配列'
  }),
  model: z.string().optional(),
  batchSize: z.number().int().min(1).max(100).optional(),
  saveToVectorize: z.boolean().optional()
})

export const BatchEmbeddingResponseSchema = z.object({
  success: z.boolean(),
  data: z.object({
    jobId: z.string(),
    workflowId: z.string(),
    status: z.string(),
    textsCount: z.number()
  }),
  message: z.string()
})

// スケジュールバッチ埋め込みスキーマ
export const ScheduleBatchEmbeddingSchema = BatchEmbeddingSchema.extend({
  delayMs: z.number().int().min(0).optional()
})

export const ScheduleBatchResponseSchema = z.object({
  success: z.boolean(),
  data: z.object({
    jobId: z.string(),
    workflowId: z.string().optional(),
    status: z.string(),
    textsCount: z.number()
  }),
  message: z.string()
})

// モデル情報スキーマ
export const ModelInfoSchema = z.object({
  name: z.string(),
  description: z.string(),
  dimensions: z.number(),
  maxTokens: z.number(),
  recommended: z.boolean()
})

export const ListModelsResponseSchema = z.object({
  success: z.boolean(),
  data: z.array(ModelInfoSchema)
})

// 型定義のエクスポート
export type GenerateEmbedding = z.infer<typeof GenerateEmbeddingSchema>
export type GenerateEmbeddingResponse = z.infer<typeof GenerateEmbeddingResponseSchema>
export type BatchEmbedding = z.infer<typeof BatchEmbeddingSchema>
export type BatchEmbeddingResponse = z.infer<typeof BatchEmbeddingResponseSchema>
export type ScheduleBatchEmbedding = z.infer<typeof ScheduleBatchEmbeddingSchema>
export type ScheduleBatchResponse = z.infer<typeof ScheduleBatchResponseSchema>
export type ModelInfo = z.infer<typeof ModelInfoSchema>
export type ListModelsResponse = z.infer<typeof ListModelsResponseSchema>

import { createRoute, RouteHandler } from '@hono/zod-openapi'
import { ErrorResponseSchema, type ErrorResponse } from '../../../schemas/error.schema'
import {
  GenerateEmbeddingSchema,
  GenerateEmbeddingResponseSchema,
  type GenerateEmbedding,
  type GenerateEmbeddingResponse
} from '../../../schemas/embedding.schema'
import { AIEmbeddings } from '@/durable-objects'

// 環境の型定義
type EnvType = {
  Bindings: Env
}

// 埋め込み生成ロジック
export async function generateEmbedding(
  params: GenerateEmbedding,
  aiEmbeddings: DurableObjectStub<AIEmbeddings>
): Promise<GenerateEmbeddingResponse> {
  const result = await aiEmbeddings.generateEmbedding(params.text, params.model)

  return {
    success: true,
    data: result,
    message: 'テキストの処理を開始しました'
  }
}

// 埋め込み生成ルート
export const generateEmbeddingRoute = createRoute({
  method: 'post',
  path: '/embeddings',
  request: {
    body: {
      content: {
        'application/json': {
          schema: GenerateEmbeddingSchema
        }
      }
    }
  },
  responses: {
    200: {
      content: {
        'application/json': {
          schema: GenerateEmbeddingResponseSchema
        }
      },
      description: '処理が開始されました'
    },
    400: {
      content: {
        'application/json': {
          schema: ErrorResponseSchema
        }
      },
      description: '不正なリクエスト'
    },
    500: {
      content: {
        'application/json': {
          schema: ErrorResponseSchema
        }
      },
      description: 'サーバーエラー'
    }
  },
  tags: ['Embeddings'],
  summary: 'テキスト埋め込み生成',
  description: 'Workers AIを使用してテキストの埋め込みベクトルを非同期で生成します。処理状況はWorkflow IDで確認できます。'
})

// 埋め込み生成ハンドラー
export const generateEmbeddingHandler: RouteHandler<typeof generateEmbeddingRoute, EnvType> = async (c) => {
  try {
    const body = c.req.valid('json')

    // Durable Objectを使用
    const aiEmbeddingsId = c.env.AI_EMBEDDINGS.idFromName('default')
    const aiEmbeddings = c.env.AI_EMBEDDINGS.get(aiEmbeddingsId)

    const response = await generateEmbedding(body, aiEmbeddings)
    return c.json(response, 200)
  } catch (error) {
    console.error('Embedding generation error:', error)
    return c.json<ErrorResponse, 500>({
      success: false,
      error: 'Internal Server Error',
      message: error instanceof Error ? error.message : '埋め込み生成中にエラーが発生しました'
    }, 500)
  }
}
