import { z } from '@hono/zod-openapi'

export const VectorMetadataSchema = z.object({
  title: z.string().optional(),
  content: z.string().optional(),
  url: z.string().url().optional(),
  timestamp: z.string().datetime().optional(),
}).passthrough()

export const VectorSchema = z.object({
  id: z.string().min(1).openapi({
    example: 'vec_123456',
    description: 'ベクトルの一意識別子'
  }),
  values: z.union([
    z.array(z.number()),
    z.any() // VectorFloatArray (Float32Array)
  ]).openapi({
    example: [0.1, 0.2, 0.3],
    description: 'ベクトルの数値配列またはFloat32Array'
  }),
  namespace: z.string().optional().openapi({
    example: 'default',
    description: 'ベクトルの名前空間'
  }),
  metadata: VectorMetadataSchema.optional().openapi({
    description: 'ベクトルに関連付けられたメタデータ'
  })
})

export const CreateVectorSchema = z.object({
  text: z.string().min(1).openapi({
    example: 'これはサンプルテキストです',
    description: 'ベクトル化するテキスト'
  }),
  model: z.string().optional().openapi({
    example: '@cf/baai/bge-base-en-v1.5',
    description: '使用するモデル名'
  }),
  namespace: z.string().optional().openapi({
    example: 'default',
    description: 'ベクトルの名前空間'
  }),
  metadata: VectorMetadataSchema.optional().openapi({
    description: 'ベクトルに関連付けるメタデータ'
  })
})

export const VectorResponseSchema = z.object({
  success: z.boolean(),
  data: VectorSchema,
  message: z.string().optional()
})

export const VectorListResponseSchema = z.object({
  success: z.boolean(),
  data: z.array(VectorSchema),
  count: z.number(),
  message: z.string().optional()
})

export const DeleteVectorResponseSchema = z.object({
  success: z.boolean(),
  deletedCount: z.number(),
  message: z.string().optional()
})

// 非同期操作レスポンススキーマ
export const AsyncVectorOperationResponseSchema = z.object({
  success: z.boolean(),
  data: z.object({
    jobId: z.string(),
    workflowId: z.string(),
    status: z.string(),
    message: z.string()
  })
})

export type Vector = z.infer<typeof VectorSchema>
export type CreateVector = z.infer<typeof CreateVectorSchema>
export type VectorResponse = z.infer<typeof VectorResponseSchema>
export type VectorListResponse = z.infer<typeof VectorListResponseSchema>
export type DeleteVectorResponse = z.infer<typeof DeleteVectorResponseSchema>
export type VectorMetadata = z.infer<typeof VectorMetadataSchema>
export type AsyncVectorOperationResponse = z.infer<typeof AsyncVectorOperationResponseSchema>

import { z } from '@hono/zod-openapi'

export const ErrorResponseSchema = z.object({
  success: z.literal(false),
  error: z.string().openapi({
    example: 'Bad Request',
    description: 'エラーの種類'
  }),
  message: z.string().openapi({
    example: '無効なリクエストパラメータです',
    description: 'エラーの詳細メッセージ'
  }),
  details: z.any().optional().openapi({
    description: 'エラーの詳細情報'
  })
})

export type ErrorResponse = z.infer<typeof ErrorResponseSchema>

export class VectorizeService {
  constructor(
    private env: Env
  ) {}

  async insert(vectors: VectorizeVector[]): Promise<void> {
    await this.env.VECTORIZE_INDEX.insert(vectors)
  }

  async upsert(vectors: VectorizeVector[]): Promise<void> {
    await this.env.VECTORIZE_INDEX.upsert(vectors)
  }

  async query(
    vector: number[],
    options?: VectorizeQueryOptions
  ): Promise<VectorizeMatches> {
    return await this.env.VECTORIZE_INDEX.query(vector, options)
  }

  async getByIds(ids: string[]): Promise<VectorizeVector[]> {
    return await this.env.VECTORIZE_INDEX.getByIds(ids)
  }

  async deleteByIds(ids: string[]): Promise<{ count: number }> {
    return await this.env.VECTORIZE_INDEX.deleteByIds(ids)
  }

  async findSimilar(
    vectorId: string,
    options?: VectorizeQueryOptions & { excludeSelf?: boolean }
  ): Promise<VectorizeMatches> {
    const vectors = await this.getByIds([vectorId])
    if (!vectors || vectors.length === 0) {
      throw new Error(`Vector ${vectorId} not found`)
    }

    const queryOptions: VectorizeQueryOptions = {
      topK: options?.excludeSelf ? (options.topK || 10) + 1 : options?.topK || 10,
      namespace: options?.namespace || vectors[0].namespace,
      returnMetadata: options?.returnMetadata ?? true,
      filter: options?.filter
    }

    const results = await this.query(vectors[0].values as number[], queryOptions)

    if (options?.excludeSelf) {
      results.matches = results.matches
        .filter(match => match.id !== vectorId)
        .slice(0, options.topK || 10)
    }

    return results
  }

  generateVectorId(prefix: string = 'vec'): string {
    const timestamp = Date.now()
    const randomStr = Math.random().toString(36).substr(2, 9)
    return `${prefix}_${timestamp}_${randomStr}`
  }
}

import { createRoute, RouteHandler } from '@hono/zod-openapi'
import { z } from '@hono/zod-openapi'
import {
  VectorResponseSchema,
  type VectorResponse
} from '../../../schemas/vector.schema'
import { ErrorResponseSchema, type ErrorResponse } from '../../../schemas/error.schema'
import { VectorizeService } from '../../../services'

// 環境の型定義
type EnvType = {
  Bindings: Env
}

// ベクトル取得ルート定義
export const getVectorRoute = createRoute({
  method: 'get',
  path: '/vectors/{id}',
  request: {
    params: z.object({
      id: z.string().min(1)
    })
  },
  responses: {
    200: {
      content: {
        'application/json': {
          schema: VectorResponseSchema
        }
      },
      description: 'ベクトル情報'
    },
    404: {
      content: {
        'application/json': {
          schema: ErrorResponseSchema
        }
      },
      description: 'ベクトルが見つかりません'
    },
    500: {
      content: {
        'application/json': {
          schema: ErrorResponseSchema
        }
      },
      description: 'サーバーエラー'
    }
  },
  tags: ['Vectors'],
  summary: 'ベクトルの取得',
  description: 'IDを指定してベクトルを取得します'
})

// ベクトル取得ハンドラー
export const getVectorHandler: RouteHandler<typeof getVectorRoute, EnvType> = async (c) => {
  try {
    const { id } = c.req.valid('param')

    const vectorizeService = new VectorizeService(c.env)
    const vectors = await vectorizeService.getByIds([id])

    if (!vectors || vectors.length === 0) {
      return c.json<ErrorResponse, 404>({
        success: false,
        error: 'Not Found',
        message: `ベクトル ${id} が見つかりません`
      }, 404)
    }

    return c.json<VectorResponse, 200>({
      success: true,
      data: vectors[0],
      message: 'ベクトルが見つかりました'
    }, 200)
  } catch (error) {
    console.error('Get vector error:', error)
    return c.json<ErrorResponse, 500>({
      success: false,
      error: 'Internal Server Error',
      message: error instanceof Error ? error.message : 'ベクトル取得中にエラーが発生しました'
    }, 500)
  }
}
