import { z } from '@hono/zod-openapi'
import { VectorMetadataSchema } from './vector.schema'

// サポートするファイルタイプ
export const SupportedFileTypes = z.enum(['application/pdf', 'image/jpeg', 'image/png', 'image/gif', 'image/webp'])

// ファイルアップロードのフォームデータスキーマ
export const FileUploadSchema = z.object({
  file: z.instanceof(File).refine(
    (file) => file.size <= 10 * 1024 * 1024, // 10MB max
    { message: 'ファイルサイズは10MB以下にしてください' }
  ),
  namespace: z.string().optional(),
  metadata: z.string().optional() // JSON string として受け取る
})

// ファイル処理レスポンススキーマ
export const FileProcessingResponseSchema = z.object({
  success: z.boolean(),
  data: z.object({
    jobId: z.string(),
    workflowId: z.string(),
    status: z.string(),
    fileInfo: z.object({
      name: z.string(),
      type: z.string(),
      size: z.number()
    }),
    message: z.string()
  })
})

// ファイル処理結果スキーマ
export const FileProcessingResultSchema = z.object({
  type: z.enum(['pdf', 'image']),
  success: z.boolean(),
  content: z.object({
    text: z.string().optional(),
    description: z.string().optional(),
    extractedPages: z.number().optional(),
    metadata: z.record(z.string(), z.any()).optional()
  }),
  vectorIds: z.array(z.string()),
  error: z.string().optional()
})

export type SupportedFileType = z.infer<typeof SupportedFileTypes>
export type FileUpload = z.infer<typeof FileUploadSchema>
export type FileProcessingResponse = z.infer<typeof FileProcessingResponseSchema>
export type FileProcessingResult = z.infer<typeof FileProcessingResultSchema>

import { z } from '@hono/zod-openapi'

export const ErrorResponseSchema = z.object({
  success: z.literal(false),
  error: z.string().openapi({
    example: 'Bad Request',
    description: 'エラーの種類'
  }),
  message: z.string().openapi({
    example: '無効なリクエストパラメータです',
    description: 'エラーの詳細メッセージ'
  }),
  details: z.any().optional().openapi({
    description: 'エラーの詳細情報'
  })
})

export type ErrorResponse = z.infer<typeof ErrorResponseSchema>

import { createRoute, RouteHandler } from '@hono/zod-openapi'
import { z } from '@hono/zod-openapi'
import {
  FileProcessingResponseSchema,
  SupportedFileTypes,
  type FileProcessingResponse
} from '../../../schemas/file-upload.schema'
import { ErrorResponseSchema, type ErrorResponse } from '../../../schemas/error.schema'

// 環境の型定義
type EnvType = {
  Bindings: Env
}

// メタデータのバリデーションスキーマ
const MetadataSchema = z.string().nullable().optional().transform((val, ctx) => {
  if (!val) return {}
  try {
    return JSON.parse(val)
  } catch {
    ctx.addIssue({
      code: z.ZodIssueCode.custom,
      message: 'メタデータのJSON形式が不正です'
    })
    return z.NEVER
  }
})

// ファイルアップロードルート定義
export const uploadFileRoute = createRoute({
  method: 'post',
  path: '/files/upload',
  request: {
    body: {
      content: {
        'multipart/form-data': {
          schema: z.object({
            file: z.instanceof(File),
            namespace: z.string().optional(),
            metadata: z.string().optional() // JSON string
          })
        }
      }
    }
  },
  responses: {
    202: {
      content: {
        'application/json': {
          schema: FileProcessingResponseSchema
        }
      },
      description: 'ファイル処理が開始されました'
    },
    400: {
      content: {
        'application/json': {
          schema: ErrorResponseSchema
        }
      },
      description: '不正なリクエスト'
    },
    413: {
      content: {
        'application/json': {
          schema: ErrorResponseSchema
        }
      },
      description: 'ファイルサイズが大きすぎます'
    },
    415: {
      content: {
        'application/json': {
          schema: ErrorResponseSchema
        }
      },
      description: 'サポートされていないファイル形式'
    },
    500: {
      content: {
        'application/json': {
          schema: ErrorResponseSchema
        }
      },
      description: 'サーバーエラー'
    }
  },
  tags: ['Files'],
  summary: 'ファイルアップロード',
  description: 'PDFまたは画像ファイルをアップロードして、内容を抽出しベクトル化します'
})

// ファイルアップロードハンドラー
export const uploadFileHandler: RouteHandler<typeof uploadFileRoute, EnvType> = async (c) => {
  try {
    const formData = await c.req.formData()
    const file = formData.get('file') as File
    const namespace = formData.get('namespace') as string | null
    const metadataStr = formData.get('metadata') as string | null

    // ファイルサイズチェック (10MB)
    if (file.size > 10 * 1024 * 1024) {
      return c.json<ErrorResponse, 413>({
        success: false,
        error: 'Payload Too Large',
        message: 'ファイルサイズは10MB以下にしてください'
      }, 413)
    }

    // ファイルタイプチェック
    const fileType = file.type
    const supportedTypes = ['application/pdf', 'image/jpeg', 'image/png', 'image/gif', 'image/webp']
    if (!supportedTypes.includes(fileType)) {
      return c.json<ErrorResponse, 415>({
        success: false,
        error: 'Unsupported Media Type',
        message: `サポートされていないファイル形式です: ${fileType}`
      }, 415)
    }

    // メタデータのパースとバリデーション
    const parseResult = MetadataSchema.safeParse(metadataStr)
    if (!parseResult.success) {
      const firstError = parseResult.error.errors?.[0]
      return c.json<ErrorResponse, 400>({
        success: false,
        error: 'Bad Request',
        message: firstError?.message || 'メタデータのバリデーションに失敗しました'
      }, 400)
    }
    const validatedMetadata = parseResult.data

    // ファイルをBase64エンコード
    const arrayBuffer = await file.arrayBuffer()
    const fileDataBase64 = btoa(String.fromCharCode(...new Uint8Array(arrayBuffer)))

    // VectorManagerを使用してファイルを処理
    const vectorManagerId = c.env.VECTOR_CACHE.idFromName('global')
    const vectorManager = c.env.VECTOR_CACHE.get(vectorManagerId)

    const result = await vectorManager.processFileAsync(
      fileDataBase64,
      file.name,
      file.type,
      file.size,
      namespace || undefined,
      validatedMetadata
    )

    return c.json<FileProcessingResponse, 202>({
      success: true,
      data: {
        jobId: result.jobId,
        workflowId: result.workflowId,
        status: result.status,
        fileInfo: {
          name: file.name,
          type: file.type,
          size: file.size
        },
        message: 'ファイルの処理を開始しました'
      }
    }, 202)
  } catch (error) {
    console.error('File upload error:', error)
    return c.json<ErrorResponse, 500>({
      success: false,
      error: 'Internal Server Error',
      message: error instanceof Error ? error.message : 'ファイルアップロード中にエラーが発生しました'
    }, 500)
  }
}

import { createRoute, RouteHandler } from '@hono/zod-openapi'
import { z } from '@hono/zod-openapi'
import { FileProcessingResultSchema } from '../../../schemas/file-upload.schema'
import { ErrorResponseSchema, type ErrorResponse } from '../../../schemas/error.schema'

// 環境の型定義
type EnvType = {
  Bindings: Env
}

// ファイル処理状況確認ルート定義
export const fileStatusRoute = createRoute({
  method: 'get',
  path: '/files/status/{workflowId}',
  request: {
    params: z.object({
      workflowId: z.string().min(1)
    })
  },
  responses: {
    200: {
      content: {
        'application/json': {
          schema: z.object({
            success: z.boolean(),
            data: z.object({
              workflowId: z.string(),
              status: z.enum(['running', 'completed', 'failed', 'unknown']),
              result: FileProcessingResultSchema.optional(),
              error: z.string().optional()
            })
          })
        }
      },
      description: '処理状況'
    },
    404: {
      content: {
        'application/json': {
          schema: ErrorResponseSchema
        }
      },
      description: 'ワークフローが見つかりません'
    },
    500: {
      content: {
        'application/json': {
          schema: ErrorResponseSchema
        }
      },
      description: 'サーバーエラー'
    }
  },
  tags: ['Files'],
  summary: 'ファイル処理状況確認',
  description: 'アップロードされたファイルの処理状況を確認します'
})

// ファイル処理状況確認ハンドラー
export const fileStatusHandler: RouteHandler<typeof fileStatusRoute, EnvType> = async (c) => {
  try {
    const { workflowId } = c.req.valid('param')

    // VectorManagerからジョブ情報を取得
    const vectorManagerId = c.env.VECTOR_CACHE.idFromName('global')
    const vectorManager = c.env.VECTOR_CACHE.get(vectorManagerId)

    // ジョブ情報を取得
    const job = await vectorManager.getFileProcessingJob(workflowId)
    if (!job) {
      return c.json<ErrorResponse, 404>({
        success: false,
        error: 'Not Found',
        message: 'ジョブが見つかりません'
      }, 404)
    }

    // Workflowの状態を取得
    const status = await vectorManager.getFileProcessingWorkflowStatus(workflowId)

    // ステータスをマッピング
    let mappedStatus: 'running' | 'completed' | 'failed' | 'unknown'
    if (job.status === 'processing' || status.status === 'running') {
      mappedStatus = 'running'
    } else if (job.status === 'completed' || status.status === 'complete') {
      mappedStatus = 'completed'
    } else if (job.status === 'failed' || status.status === 'errored') {
      mappedStatus = 'failed'
    } else {
      mappedStatus = 'unknown'
    }

    return c.json({
      success: true,
      data: {
        workflowId,
        status: mappedStatus,
        result: status.output ? FileProcessingResultSchema.parse(status.output) : undefined,
        error: job.error || status.error || undefined
      }
    }, 200)
  } catch (error) {
    // Workflowが見つからない場合
    if (error instanceof Error && error.message.includes('not found')) {
      return c.json<ErrorResponse, 404>({
        success: false,
        error: 'Not Found',
        message: 'ワークフローが見つかりません'
      }, 404)
    }

    console.error('File status error:', error)
    return c.json<ErrorResponse, 500>({
      success: false,
      error: 'Internal Server Error',
      message: error instanceof Error ? error.message : '状況確認中にエラーが発生しました'
    }, 500)
  }
}

import type { OpenAPIHono } from '@hono/zod-openapi'
import { uploadFileRoute, uploadFileHandler } from './upload'
import { fileStatusRoute, fileStatusHandler } from './status'

// エクスポート用関数
export default (app: OpenAPIHono<{ Bindings: Env }>) => {
  app.openapi(uploadFileRoute, uploadFileHandler)
  app.openapi(fileStatusRoute, fileStatusHandler)
}
