import { z } from 'zod'

// Notion User
export const NotionUserSchema = z.object({
  object: z.literal('user'),
  id: z.string()
})

// Notion Parent
export const NotionParentSchema = z.discriminatedUnion('type', [
  z.object({
    type: z.literal('database_id'),
    database_id: z.string()
  }),
  z.object({
    type: z.literal('page_id'),
    page_id: z.string()
  }),
  z.object({
    type: z.literal('workspace'),
    workspace: z.literal(true)
  }),
  z.object({
    type: z.literal('block_id'),
    block_id: z.string()
  })
])

// Notion Page
export const NotionPageSchema = z.object({
  object: z.literal('page'),
  id: z.string(),
  created_time: z.string(),
  last_edited_time: z.string(),
  created_by: NotionUserSchema,
  last_edited_by: NotionUserSchema,
  cover: z.any().nullable(),
  icon: z.any().nullable(),
  parent: NotionParentSchema,
  archived: z.boolean(),
  in_trash: z.boolean(),
  properties: z.record(z.string(), z.any()),
  url: z.string(),
  public_url: z.string().nullable().optional()
})

// Notion Block Types
export const NotionBlockTypeSchema = z.enum([
  'paragraph',
  'heading_1',
  'heading_2', 
  'heading_3',
  'bulleted_list_item',
  'numbered_list_item',
  'to_do',
  'toggle',
  'child_page',
  'child_database',
  'embed',
  'image',
  'video',
  'file',
  'pdf',
  'bookmark',
  'callout',
  'quote',
  'equation',
  'divider',
  'table_of_contents',
  'column',
  'column_list',
  'link_preview',
  'synced_block',
  'template',
  'link_to_page',
  'table',
  'table_row',
  'code',
  'unsupported'
])

// Rich Text
export const RichTextSchema = z.object({
  type: z.enum(['text', 'mention', 'equation']),
  text: z.object({
    content: z.string(),
    link: z.object({ url: z.string() }).nullable().optional()
  }).optional(),
  mention: z.any().optional(),
  equation: z.any().optional(),
  annotations: z.object({
    bold: z.boolean(),
    italic: z.boolean(),
    strikethrough: z.boolean(),
    underline: z.boolean(),
    code: z.boolean(),
    color: z.string()
  }).optional(),
  plain_text: z.string(),
  href: z.string().nullable().optional()
})

// Notion Block
export const NotionBlockSchema = z.object({
  object: z.literal('block'),
  id: z.string(),
  parent: z.object({
    type: z.string(),
    page_id: z.string().optional(),
    block_id: z.string().optional()
  }),
  created_time: z.string(),
  last_edited_time: z.string(),
  created_by: NotionUserSchema,
  last_edited_by: NotionUserSchema,
  has_children: z.boolean(),
  archived: z.boolean(),
  in_trash: z.boolean(),
  type: NotionBlockTypeSchema
}).passthrough() // Allow block type specific properties

// API Request Schemas
export const RetrievePageRequestSchema = z.object({
  pageId: z.string()
})

export const RetrievePagePropertyRequestSchema = z.object({
  pageId: z.string(),
  propertyId: z.string()
})

export const SyncNotionPageRequestSchema = z.object({
  pageId: z.string(),
  includeBlocks: z.boolean().default(true),
  includeProperties: z.boolean().default(true),
  namespace: z.string().optional()
})

// Response Schemas
export const NotionPageResponseSchema = z.object({
  success: z.boolean(),
  data: NotionPageSchema.optional(),
  message: z.string().optional()
})

export const NotionBlockListResponseSchema = z.object({
  success: z.boolean(),
  data: z.object({
    blocks: z.array(NotionBlockSchema),
    has_more: z.boolean(),
    next_cursor: z.string().nullable()
  }).optional(),
  message: z.string().optional()
})

export const NotionSyncResponseSchema = z.object({
  success: z.boolean(),
  data: z.object({
    jobId: z.string(),
    pageId: z.string(),
    status: z.string(),
    message: z.string()
  }).optional(),
  message: z.string().optional()
})

// Types
export type NotionPage = z.infer<typeof NotionPageSchema>
export type NotionBlock = z.infer<typeof NotionBlockSchema>
export type NotionBlockType = z.infer<typeof NotionBlockTypeSchema>
export type RichText = z.infer<typeof RichTextSchema>
export type RetrievePageRequest = z.infer<typeof RetrievePageRequestSchema>
export type RetrievePagePropertyRequest = z.infer<typeof RetrievePagePropertyRequestSchema>
export type SyncNotionPageRequest = z.infer<typeof SyncNotionPageRequestSchema>
export type NotionPageResponse = z.infer<typeof NotionPageResponseSchema>
export type NotionBlockListResponse = z.infer<typeof NotionBlockListResponseSchema>
export type NotionSyncResponse = z.infer<typeof NotionSyncResponseSchema>

import { z } from '@hono/zod-openapi'

export const ErrorResponseSchema = z.object({
  success: z.literal(false),
  error: z.string().openapi({
    example: 'Bad Request',
    description: 'エラーの種類'
  }),
  message: z.string().openapi({
    example: '無効なリクエストパラメータです',
    description: 'エラーの詳細メッセージ'
  }),
  details: z.any().optional().openapi({
    description: 'エラーの詳細情報'
  })
})

export type ErrorResponse = z.infer<typeof ErrorResponseSchema>

import { drizzle } from 'drizzle-orm/d1'
import * as schema from './schema'

export function getDb(env: Env) {
  return drizzle(env.DB, { schema })
}

export * from './schema'

import { getDb } from '../db'
import type { 
  NotionPage, 
  NotionBlock,
  NewNotionPage,
  NewNotionBlock,
  NewNotionPageProperty,
  NewNotionVectorRelation
} from '../db/schema'
import { 
  notionPages, 
  notionBlocks, 
  notionPageProperties,
  notionVectorRelations,
  notionSyncJobs
} from '../db/schema'
import { eq } from 'drizzle-orm'
import type { NotionPage as NotionAPIPage, NotionBlock as NotionAPIBlock } from '../schemas/notion.schema'

export class NotionService {
  constructor(
    private env: Env,
    private notionToken: string
  ) {}

  private get db() {
    return getDb(this.env)
  }

  // Notion API から ページを取得
  async fetchPageFromNotion(pageId: string): Promise<NotionAPIPage | null> {
    try {
      const response = await fetch(`https://api.notion.com/v1/pages/${pageId}`, {
        headers: {
          'Authorization': `Bearer ${this.notionToken}`,
          'Notion-Version': '2022-06-28'
        }
      })

      if (!response.ok) {
        if (response.status === 404) return null
        throw new Error(`Notion API error: ${response.status}`)
      }

      return await response.json() as NotionAPIPage
    } catch (error) {
      console.error('Failed to fetch page from Notion:', error)
      throw error
    }
  }

  // Notion API から ブロックを取得
  async fetchBlocksFromNotion(pageId: string): Promise<NotionAPIBlock[]> {
    const blocks: NotionAPIBlock[] = []
    let cursor: string | null = null

    try {
      do {
        const url = new URL(`https://api.notion.com/v1/blocks/${pageId}/children`)
        if (cursor) url.searchParams.append('start_cursor', cursor)
        url.searchParams.append('page_size', '100')

        const response = await fetch(url.toString(), {
          headers: {
            'Authorization': `Bearer ${this.notionToken}`,
            'Notion-Version': '2022-06-28'
          }
        })

        if (!response.ok) {
          throw new Error(`Notion API error: ${response.status}`)
        }

        const data = await response.json() as {
          results: NotionAPIBlock[]
          has_more: boolean
          next_cursor: string | null
        }

        blocks.push(...data.results)
        cursor = data.has_more ? data.next_cursor : null
      } while (cursor)

      return blocks
    } catch (error) {
      console.error('Failed to fetch blocks from Notion:', error)
      throw error
    }
  }

  // Notion API から プロパティを取得
  async fetchPagePropertyFromNotion(pageId: string, propertyId: string): Promise<any> {
    try {
      const response = await fetch(
        `https://api.notion.com/v1/pages/${pageId}/properties/${propertyId}`,
        {
          headers: {
            'Authorization': `Bearer ${this.notionToken}`,
            'Notion-Version': '2022-06-28'
          }
        }
      )

      if (!response.ok) {
        throw new Error(`Notion API error: ${response.status}`)
      }

      return await response.json()
    } catch (error) {
      console.error('Failed to fetch property from Notion:', error)
      throw error
    }
  }

  // ページをDBに保存
  async savePage(page: NotionAPIPage): Promise<void> {
    const newPage: NewNotionPage = {
      id: page.id,
      object: page.object,
      createdTime: page.created_time,
      lastEditedTime: page.last_edited_time,
      createdById: page.created_by.id,
      lastEditedById: page.last_edited_by.id,
      cover: page.cover ? JSON.stringify(page.cover) : null,
      icon: page.icon ? JSON.stringify(page.icon) : null,
      parent: JSON.stringify(page.parent),
      archived: page.archived,
      inTrash: page.in_trash,
      properties: JSON.stringify(page.properties),
      url: page.url,
      publicUrl: page.public_url || null
    }

    await this.db.insert(notionPages)
      .values(newPage)
      .onConflictDoUpdate({
        target: notionPages.id,
        set: newPage
      })
  }

  // ブロックをDBに保存
  async saveBlocks(pageId: string, blocks: NotionAPIBlock[]): Promise<void> {
    if (blocks.length === 0) return

    const newBlocks: NewNotionBlock[] = blocks.map((block, index) => ({
      id: block.id,
      pageId: pageId,
      object: block.object,
      type: block.type,
      createdTime: block.created_time,
      lastEditedTime: block.last_edited_time,
      createdById: block.created_by.id,
      lastEditedById: block.last_edited_by.id,
      hasChildren: block.has_children,
      archived: block.archived,
      inTrash: block.in_trash,
      parentId: block.parent.block_id || pageId,
      parentType: block.parent.type,
      content: JSON.stringify(block),
      plainText: this.extractPlainTextFromBlock(block),
      orderIndex: index
    }))

    // バッチで挿入
    for (const block of newBlocks) {
      await this.db.insert(notionBlocks)
        .values(block)
        .onConflictDoUpdate({
          target: notionBlocks.id,
          set: block
        })
    }
  }

  // プロパティをDBに保存
  async savePageProperties(pageId: string, properties: Record<string, any>): Promise<void> {
    const propertyEntries = Object.entries(properties)

    for (const [propertyName, propertyData] of propertyEntries) {
      const propertyId = propertyData.id
      const propertyType = propertyData.type
      const plainText = this.extractPlainTextFromProperty(propertyData)
      const numberValue = propertyType === 'number' ? propertyData.number : null

      const newProperty: NewNotionPageProperty = {
        id: `${pageId}_${propertyId}`,
        pageId,
        propertyId,
        propertyName,
        propertyType,
        propertyValue: JSON.stringify(propertyData),
        plainTextValue: plainText,
        numberValue
      }

      await this.db.insert(notionPageProperties)
        .values(newProperty)
        .onConflictDoUpdate({
          target: notionPageProperties.id,
          set: newProperty
        })
    }
  }

  // ベクトル関連を保存
  async saveVectorRelation(
    notionPageId: string,
    vectorId: string,
    vectorNamespace: string,
    contentType: string,
    notionBlockId?: string
  ): Promise<void> {
    const newRelation: NewNotionVectorRelation = {
      notionPageId,
      notionBlockId,
      vectorId,
      vectorNamespace,
      contentType
    }

    await this.db.insert(notionVectorRelations).values(newRelation)
  }

  // ページを取得（DBから）
  async getPage(pageId: string): Promise<NotionPage | null> {
    const result = await this.db.select()
      .from(notionPages)
      .where(eq(notionPages.id, pageId))
      .limit(1)

    return result[0] || null
  }

  // すべてのページを取得（DBから）
  async getAllPagesFromCache(options: {
    archived?: boolean
    limit?: number
  } = {}): Promise<NotionPage[]> {
    const conditions = []
    if (options.archived !== undefined) {
      conditions.push(eq(notionPages.archived, options.archived))
    }

    const baseQuery = this.db
      .select()
      .from(notionPages)
      .orderBy(notionPages.lastEditedTime)

    if (conditions.length > 0) {
      const queryWithWhere = baseQuery.where(conditions[0])
      if (options.limit) {
        return await queryWithWhere.limit(options.limit)
      }
      return await queryWithWhere
    }

    if (options.limit) {
      return await baseQuery.limit(options.limit)
    }
    return await baseQuery
  }

  // Notion APIで全ページを検索
  async searchAllPages(options: {
    start_cursor?: string
    page_size?: number
    filter?: {
      property: string
      value: string
    }
  } = {}): Promise<{
    results: NotionAPIPage[]
    has_more: boolean
    next_cursor: string | null
  }> {
    try {
      const searchBody: any = {
        page_size: options.page_size || 100
      }

      if (options.start_cursor) {
        searchBody.start_cursor = options.start_cursor
      }

      if (options.filter) {
        searchBody.filter = {
          property: options.filter.property,
          [options.filter.property]: {
            equals: options.filter.value
          }
        }
      }

      const response = await fetch('https://api.notion.com/v1/search', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${this.notionToken}`,
          'Notion-Version': '2022-06-28',
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(searchBody)
      })

      if (!response.ok) {
        throw new Error(`Notion API error: ${response.status}`)
      }

      const data = await response.json() as {
        results: any[]
        has_more: boolean
        next_cursor: string | null
      }

      return {
        results: data.results.filter((item) => item.object === 'page'),
        has_more: data.has_more,
        next_cursor: data.next_cursor
      }
    } catch (error) {
      console.error('Failed to search pages from Notion:', error)
      throw error
    }
  }

  // ブロックを取得（DBから）
  async getBlocks(pageId: string): Promise<NotionBlock[]> {
    return await this.db.select()
      .from(notionBlocks)
      .where(eq(notionBlocks.pageId, pageId))
      .orderBy(notionBlocks.orderIndex)
  }

  // ブロックからプレーンテキストを抽出
  private extractPlainTextFromBlock(block: NotionAPIBlock): string {
    // Type-safe access without using any casting
    if (!block.type) return ''

    // Handle blocks with rich_text property
    if (this.hasRichText(block)) {
      const richText = this.getRichTextFromBlock(block)
      if (richText) {
        return richText
          .map((rt: any) => rt.plain_text || '')
          .join('')
      }
    }

    // Handle table_row blocks
    if (block.type === 'table_row') {
      const tableRowBlock = block as any
      const content = tableRowBlock.table_row
      if (content && content.cells) {
        return content.cells
          .map((cell: any[]) => 
            cell.map((rt: any) => rt.plain_text || '').join('')
          )
          .join(' ')
      }
    }

    return ''
  }

  // Helper method to check if block has rich_text
  private hasRichText(block: NotionAPIBlock): boolean {
    const richTextTypes = [
      'paragraph', 'heading_1', 'heading_2', 'heading_3',
      'bulleted_list_item', 'numbered_list_item', 'to_do',
      'toggle', 'quote', 'callout', 'code'
    ]
    return richTextTypes.includes(block.type)
  }

  // Helper method to get rich_text content from block
  private getRichTextFromBlock(block: NotionAPIBlock): any[] | null {
    const blockContent = (block as any)[block.type]
    return blockContent && blockContent.rich_text ? blockContent.rich_text : null
  }

  // プロパティからプレーンテキストを抽出
  private extractPlainTextFromProperty(property: any): string {
    if (!property) return ''

    switch (property.type) {
      case 'title':
      case 'rich_text':
        return property[property.type]
          ?.map((rt: any) => rt.plain_text || '')
          .join('') || ''

      case 'number':
        return property.number?.toString() || ''

      case 'select':
        return property.select?.name || ''

      case 'multi_select':
        return property.multi_select
          ?.map((s: any) => s.name)
          .join(', ') || ''

      case 'date':
        return property.date?.start || ''

      case 'people':
        return property.people
          ?.map((p: any) => p.name || p.id)
          .join(', ') || ''

      case 'url':
      case 'email':
      case 'phone_number':
        return property[property.type] || ''

      default:
        return ''
    }
  }

  // ページタイトルを抽出
  extractPageTitle(properties: Record<string, any>): string {
    // タイトルプロパティを見つける
    const titleProperty = Object.values(properties).find(
      (prop: any) => prop.type === 'title'
    ) as any

    if (!titleProperty || !titleProperty.title) {
      return 'Untitled'
    }

    return titleProperty.title
      .map((rt: any) => rt.plain_text || '')
      .join('') || 'Untitled'
  }
}

import { createRoute, RouteHandler } from '@hono/zod-openapi'
import { z } from '@hono/zod-openapi'
import { NotionPageResponseSchema } from '../../../schemas/notion.schema'
import { ErrorResponseSchema, type ErrorResponse } from '../../../schemas/error.schema'
import { NotionService } from '../../../services/notion.service'

// 環境の型定義
type EnvType = {
  Bindings: Env
}

// ページ取得ルート定義
export const retrieveNotionPageRoute = createRoute({
  method: 'get',
  path: '/notion/pages/{pageId}',
  request: {
    params: z.object({
      pageId: z.string().min(1)
    }),
    query: z.object({
      fromCache: z.string().optional().transform(val => val === 'true').default(false)
    })
  },
  responses: {
    200: {
      content: {
        'application/json': {
          schema: NotionPageResponseSchema
        }
      },
      description: 'ページ情報'
    },
    401: {
      content: {
        'application/json': {
          schema: ErrorResponseSchema
        }
      },
      description: '認証エラー'
    },
    404: {
      content: {
        'application/json': {
          schema: ErrorResponseSchema
        }
      },
      description: 'ページが見つかりません'
    },
    500: {
      content: {
        'application/json': {
          schema: ErrorResponseSchema
        }
      },
      description: 'サーバーエラー'
    }
  },
  tags: ['Notion'],
  summary: 'Notionページ取得',
  description: 'Notion APIまたはキャッシュからページ情報を取得します'
})

// ページ取得ハンドラー
export const retrieveNotionPageHandler: RouteHandler<typeof retrieveNotionPageRoute, EnvType> = async (c) => {
  try {
    const { pageId } = c.req.valid('param')
    const { fromCache } = c.req.valid('query')

    // Notion APIトークンを取得
    const notionToken = c.env.NOTION_API_KEY
    if (!notionToken) {
      return c.json<ErrorResponse, 401>({
        success: false,
        error: 'Unauthorized',
        message: 'Notion APIトークンが設定されていません'
      }, 401)
    }

    // NotionServiceを使用してページを取得
    const notionService = new NotionService(c.env, notionToken)

    // まずキャッシュから取得
    let page = await notionService.getPage(pageId)

    // キャッシュにない場合はNotion APIから取得
    if (!page && !fromCache) {
      const notionPage = await notionService.fetchPageFromNotion(pageId)
      if (notionPage) {
        await notionService.savePage(notionPage)
        page = await notionService.getPage(pageId)
      }
    }

    if (!page) {
      return c.json<ErrorResponse, 404>({
        success: false,
        error: 'Not Found',
        message: 'ページが見つかりません'
      }, 404)
    }

    // データベース形式の場合は変換
    if (page && !('object' in page)) {
      // キャッシュからの場合 - NotionPage型
      interface CachedPage {
        id: string
        createdTime: string
        lastEditedTime: string
        createdById: string
        lastEditedById: string
        cover: string | null
        icon: string | null
        parent: string
        archived: boolean
        inTrash: boolean
        properties: string
        url: string
        publicUrl: string | null
      }

      const cachedPage: CachedPage = page

      const objectType: 'page' = 'page'
      const userType: 'user' = 'user'

      const pageData = {
        object: objectType,
        id: cachedPage.id,
        created_time: cachedPage.createdTime,
        last_edited_time: cachedPage.lastEditedTime,
        created_by: { object: userType, id: cachedPage.createdById },
        last_edited_by: { object: userType, id: cachedPage.lastEditedById },
        cover: cachedPage.cover ? JSON.parse(cachedPage.cover) : null,
        icon: cachedPage.icon ? JSON.parse(cachedPage.icon) : null,
        parent: JSON.parse(cachedPage.parent),
        archived: cachedPage.archived,
        in_trash: cachedPage.inTrash,
        properties: JSON.parse(cachedPage.properties),
        url: cachedPage.url,
        public_url: cachedPage.publicUrl || undefined
      }

      return c.json({
        success: true,
        data: pageData
      }, 200)
    } else {
      // Notion APIから直接取得したページ
      const apiPage = await notionService.fetchPageFromNotion(pageId)
      if (!apiPage) {
        return c.json<ErrorResponse, 404>({
          success: false,
          error: 'Not Found',
          message: 'ページが見つかりません'
        }, 404)
      }

      return c.json({
        success: true,
        data: apiPage
      }, 200)
    }
  } catch (error) {
    console.error('Retrieve page error:', error)
    return c.json<ErrorResponse, 500>({
      success: false,
      error: 'Internal Server Error',
      message: error instanceof Error ? error.message : 'ページ取得中にエラーが発生しました'
    }, 500)
  }
}
